mapper.select:
SELECT COUNT(*)
	FROM auction
	WHERE endStatus = #{endStatus}

		AND categoryId = #{categoryId}


		AND (
				`name` LIKE CONCAT('%', #{searchKeyword}, '%')
				OR `description` LIKE CONCAT('%', #{searchKeyword}, '%')
			)

SELECT *
	FROM auction
    WHERE endStatus = #{endStatus}

			AND (
				`name` LIKE CONCAT('%', #{searchKeyword}, '%')
				OR `description` LIKE CONCAT('%', #{searchKeyword}, '%')
				)


			AND categoryId = #{categoryId}

	LIMIT #{limitStart}, #{itemsInAPage}

mapper.insert:
INSERT INTO auction
	SET regDate = NOW()
		, updateDate = NOW()
		, endDate = DATE_ADD(NOW(), INTERVAL #{bidDate} DAY)
		, `name` = #{name}
		, `description` = #{body}
		, memberId = #{memberId}
		, categoryId = #{categoryId}
		, startBid = #{startBid}
		, nowBid = #{startBid}
		, minimumBid = #{startBid} * 0.05
		, buyNow = #{buyNow}
		, bidDate = #{bidDate}
		, charge = #{charge}

mapper.update:
UPDATE auction
	SET updateDate = NOW() 
		, description = #{description}
	WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
	FROM auction
	WHERE endStatus = 0
	AND `name` = #{name}
	AND description = #{description}

mapper.update:
UPDATE auction SET updateDate = NOW() , minimumBid = #{bid} * 0.05 , nowBid = #{bid} , bidCount = bidCount + 1
, buyNow = 0
WHERE id = #{id}

UPDATE auction
	SET updateDate = NOW()
		, endDate = NOW()
		, endBid = #{buyNow}
		, endStatus = 1
		, buyerId = #{buyerId}
	WHERE id = #{id}
