mapper.insert:
INSERT INTO CART
	SET regDate = NOW()
		, updateDate = NOW()
		, auctionId = #{auctionId}
		, memberId = #{memberId}
		, `name` = #{name}
		, `description` = #{description}
		, memberBid = #{nowBid}

mapper.select:
SELECT COUNT(*)
	FROM cart
	WHERE memberId = #{memberId}
	AND endStatus = #{endStatus}

		AND (
				`name` LIKE CONCAT('%', #{searchKeyword}, '%')
				OR `description` LIKE CONCAT('%', #{searchKeyword}, '%')
			)

SELECT C.* , A.endDate , A.nowBid , A.endBid , A.buyNow , A.charge , A.buyerId FROM cart AS C INNER JOIN auction AS A ON C.auctionId = A.id WHERE C.memberId = #{memberId} AND endStatus = #{endStatus}
AND ( `name` LIKE CONCAT('%', #{searchKeyword}, '%') OR `description` LIKE CONCAT('%', #{searchKeyword}, '%') )
LIMIT #{limitStart}, #{itemsInAPage}
