mapper.insert:
INSERT INTO interestCategory
	SET regDate = NOW()
	, updateDate = NOW()
	, memberId = #{memberId}
	, categoryId = #{categoryId}

mapper.select:
SELECT *
	FROM category
	ORDER BY id;

mapper.delete:
DELETE
	FROM interestCategory
	WHERE categoryId = #{categoryId}
	AND memberId = #{loginedMemberId}
