mapper.insert:
INSERT INTO productFile
	SET regDate = NOW()
		, auctionType = #{auctionType}
		, auctionId = #{auctionId}
		, originName = #{orgName}
		, savedName = #{savedName}
		, savedPath = #{savedPath}

mapper.select:
SELECT *
	FROM productFile
	WHERE auctionId = #{auctionId}

		AND auctionType = #{auctionType}

SELECT *
	FROM productFile
	WHERE auctionId = #{auctionId}

		AND auctionType = #{auctionType}

	LIMIT 1
